mapper.insert:
UPDATE tbl_member SET
nickname = #{nickname} , name = #{name}, phone = #{phone}, birthYear = #{birth_year}, birthMonth = #{birth_month},
birthDay = #{birth_day}, email = #{email}, gender = #{gender}
WHERE account=#{account}

mapper.select:
SELECT COUNT(*)
FROM tbl_member

    WHERE account = #{value}


    WHERE email = #{value}


    WHERE phone = #{value}
