mapper.update:
UPDATE tbl_member SET
password=#{password}, nickname = #{nickname} , name = #{name}, phone = #{phone}, birth_year = #{birthYear}, birth_month = #{birthMonth},
birth_day = #{birthDay}, email = #{email}, gender = #{gender}, auth=#{auth}
WHERE account=#{account}

mapper.select:
SELECT COUNT(*)
FROM tbl_member

    WHERE account = #{value}


    WHERE email = #{value}


    WHERE phone = #{value}

SELECT *
FROM tbl_member

    WHERE account = #{value}


    WHERE email = #{value}


    WHERE phone = #{value}
