mapper.select:
SELECT *
FROM menu
WHERE type = #{type} AND espresso_kind = #{espressoKind}

mapper.update:
UPDATE menu
SET espresso_kind=#{espressoKind}, price=#{price}, flavor=#{flavor}, feel=#{feel}, degree=#{degree}, image=#{image}, name_kr=#{nameKr}, name_en=#{nameEn}, weight=#{weight}, kind=#{kind}, type=#{type}, description_summary=#{descriptionSummary}, description_detail=#{descriptionDetail}, design_story=#{designStory}, coffee_tasting_note=#{coffeeTastingNote}, tasting_notes=#{tastingNotes}, enjoy_with=#{enjoyWith}, relative=#{relative}, processing_method=#{processingMethod}, weight2=#{weight2}, calorie1 = #{calorie1}, calorie2 = #{calorie2}, calorie3 = #{calorie3}, calorie4 = #{calorie4}, calorie5 = #{calorie5}, calorie6=#{calorie6}
WHERE id = #{id}

UPDATE menu
SET espresso_kind=#{espressoKind}, price=#{price}, flavor=#{flavor}, feel=#{feel}, degree=#{degree}, image=#{image}, name_kr=#{nameKr}, name_en=#{nameEn}, weight=#{weight}, kind=#{kind}, type=#{type}, description_summary=#{descriptionSummary}, description_detail=#{descriptionDetail}, design_story=#{designStory}, coffee_tasting_note=#{coffeeTastingNote}, tasting_note=#{tastingNote}, enjoy_with=#{enjoyWith}, relative=#{relative}, processing_method=#{processingMethod}
WHERE id = #{id}, weight2=#{weight2}
